    fetch(`localhost:4200/uploadPicture/${name}/${password}/${file.name}/${file.type}/${file.webkitRelativePath}/${file.lastModifiedDate}`, requestOptions)
        .then(response => response.text())
        .then(result => console.log(result))
        .catch(error => console.log('error', error));
