import React, { Component } from 'react';

class ToByList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newItemToAdd: '',
      isOpen: true,
      items: ['tomatoes', 'cucumbers', 'carrots']
    }
  }
  updateNewItemToAdd = (newVal) => {
    this.setState({ newItemToAdd: newVal })
  }
  addItem=()=>{
    let clonedState=this.state.items.slice()
    clonedState.push(this.state.newItemToAdd)
    
  }
  render() {
    return (
      <div className="ToBuyList">
        <input onChange={e => this.updateNewItemToAdd(e.target.value)} />
        <button>add</button>
        <button>hide/show</button>
        <ul>
          {this.state.items.map((item, index) => <li key={index}>{item}</li>)}
        </ul>

        <div>{this.state.newItemToAdd}</div>
      </div>
    );
  }
}

export default ToByList;
