import { keyBy } from 'lodash'
import { SET_USERS, SET_SELECTED, setUsers } from '../actions/users.action'

const initialState = {
    user: {
        name: "",
        email: "",
        password: "",
        images: []
    },
}

export default function usersReducer(state = initialState, action) {
    switch (action.type) {
        case SET_USERS:
            return { ...state, user: action.payload }
        case SET_NAME:
            return { ...state, name: action.payload.name }
        case SET_NAME:
            return { ...state, name: action.payload.name }
        case SET_NAME:
            return { ...state, name: action.payload.name }
        case SET_NAME:
            return { ...state, name: action.payload.name }


        default:
            return state;
    }
}
