import { keyBy } from 'lodash'
import { SET_USERS, SET_SELECTED, setUsers } from '../actions/users.action'

const initialState = {
    users: {},
    selectedRight: 0,
    selectedLeft: 0
}

export default function usersReducer(state = initialState, action) {
    switch (action.type) {
        case SET_USERS:
            return { ...state, users: keyBy(action.payload, (user) => user.id) }

        case SET_SELECTED:
            return { ...state, ['selected' + action.payload.rightOrLeft]: action.payload.user.id }

        default:
            return state;
    }
}
