import React from 'react';
import { useState, useEffect } from 'react';
// import Image from 'react-bootstrap/Image'
import { connect } from 'react-redux';

function TodayPicture(props) {
    const [url, setUrl] = useState("")
    const [title, setTitle] = useState("")
    const [explanation, setExplanation] = useState("")

    useEffect(() => {
        var myHeaders = new Headers();
        myHeaders.append("Authorization", "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJuYW1lIjoicmluYSIsImVtYWlsIjoicmluYUBnbWFpbC5jb20iLCJwYXNzd29yZCI6Ijg4ODgiLCJpYXQiOjE2MTkyOTg4NDB9.iyih_mVLjgSmXC7qbpXYn60I_DB_urMB0Lmk3pwnLZc");

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            redirect: 'follow'
        };

        fetch(`http://localhost:4200/newPicture/${props.name}/${props.password}`, requestOptions)
            .then(response => response.text())
            .then(result => {
                let result2 = JSON.parse(result)
                setUrl(result2.newImage.url)
                setTitle(result2.newImage.title)
                setExplanation(result2.newImage.explanation)
            })
            .catch(error => console.log('error', error));
    })

    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="col">
                        <h2>picture of the day</h2>

                        <img src={`${url}`}> </img>
                        {/* <Image src={`${url}`} rounded className=" w-100" /> */}
                    </div>

                    <div className="col mt-5">
                        <h2>{title}</h2>
                        <p>{explanation}</p>
                    </div>

                </div>
            </div>
        </div>

    )
}




export default connect(
    (state) => {
        return {
            name: state.name,
            password: state.password,
            email: state.email,
            picture: state.picture
        }
    },
    null
)(TodayPicture);

