import React from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik'
import *as Yup from 'yup'
import { connect } from 'react-redux'
import {  Link } from 'react-router-dom';


const LoginSchema = Yup.object().shape({
    name: Yup.string().required('this field is requierd'),
    password: Yup.string().required('this field is requierd')
})

function Login(props) {

    const login = async (values) => {
        console.log(values);
    }
    return (
        <Formik
            initialValues={{ name: props.name, password: props.password }}
            onSubmit={login}
            validationSchema={LoginSchema}
        >
            <Form>
                <h2>Login form</h2>
                <div className="form-group">
                    <Field className="form-control" type="name" name="name" />
                    <ErrorMessage className="alert alert-danger" component="div" name="name" />
                </div>
                <div className="form-group">

                    <Field className="form-control" type="password" name="password" />
                    <ErrorMessage className="alert alert-danger" component="div" name="password" />


                </div>
                <div className="form-group">
                    <button type='submit' className="btn btn-primary">Login</button>
                </div>

                <h3>{props.name}</h3>
                <h3>{props.password}</h3>
                <h3>{props.email}</h3>
                <p>
                new User?  <Link to="/register">Register</Link>
              </p>

            </Form>
        </Formik>
    )
}




export default connect(
    (state) => {
        return {
            name: state.name,
            password: state.password,
            email: state.email
        }
    },
    null
)(Login);

function getUserByNameAndPassword(){
    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
      };
      
      fetch("localhost:4200/getByNameAndPassword/{}/5555", requestOptions)
        .then(response => response.text())
        .then(result => console.log(result))
        .catch(error => console.log('error', error));
}
