import {  SET_NAME, SET_EMAIL, SET_PASSWORD, SET_PICTURES } from '../actions/action'

const initialState = {
    // user: {
        name: "",
        email: "",
        password: "",
        pictures: []
    // }
}

export default function reducer(state = initialState, action) {

    switch (action.type) {
        case SET_NAME:
            state.user.name = action.payload;
            console.log(`hi ${state.user.name}`);
            break;
        case SET_EMAIL:
            state.user.email = action.payload;
            console.log(`hi ${state.user.email}`);

            break;

        case SET_PASSWORD:
            state.user.password = action.payload;
            console.log(`hi ${state.user.password}`);
            break;

        case SET_PICTURES:
            state.user.pictures = action.payload;
            break;



        default:
            return state;
    }
}
