              <Route component={Register} path="/register" />
              <Route component={TodayPicture} path="/login/todayPicture"/>
              <Route component={HistoryPictuer} path="/login/historyPicture" />
              <Route component={Picture} path="/login/picture" />
              <Route component={Login} path="/" />
            </Switch>
          </div> */}



          {
            authenticated ?
              <NavBar/>
              :
              <Login authenticated={setAuthenticated} />
          }

          <Switch>
            <Route component={NavBar} path="/navbar" />
            <Route component={Register} path="/register" />
            <Route component={TodayPicture} path="/todayPicture" render={(props) => (authenticated ? <TodayPicture {...props} /> : <Redirect to="/login" />)}  />
            <Route component={HistoryPictuer} render={(props) => (authenticated ? <HistoryPictuer {...props} /> : <Redirect to="/login" />)} path="/historyPicture" />
            <Route component={Picture} render={(props) => (authenticated ? <Picture {...props} /> : <Redirect to="/login" />)} path="/picture" />
           <Route path="/login" component={Login}></Route>
            <Route component={Login} e path="/" />
          </Switch>

          {/* <div className="col-6">
              <Register />
            </div> */}
          {/* </Provider> */}

        </div>
        {/* <div id="carouselExampleControls" className="carousel slide" data-ride="carousel">
          <div className="carousel-inner">
            <div className="carousel-item active">
              <img style={{opacity:0.5,display:'flex'}} className="d-block w-100" src="1.jpg" alt="First slide" />
            </div>
            <div className="carousel-item">
              <img className="d-block w-100" src="2.jpg" alt="Second  slide" />
            </div>
            <div className="carousel-item">
              <img className="d-block w-100" src="3.jpg" alt="Third  slide" />
            </div>
          </div>
        </div> */}
      </div>
      </Router>
    </div>
  );
}






