import React, { useState } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik'
import *as Yup from 'yup'
import userService from '../services/user.service'


const RegisterSchema = Yup.object().shape({
    firstName: Yup.string().required('this field is requierd').min(2),
    lastName: Yup.string().required('this field is requierd').min(2),
    email: Yup.string().required('this field is requierd').email('invalid mail'),
    password: Yup.string().required('this field is requierd')
})

export default ({tokenCb}) => {

    const [errorText, setError] = useState(null)

    const register = async (values) => {
        console.log(`${values.firstName} ${values.lastName} ${values.email} ${values.password}`);
        try {
            const token = await userService.register(values)
            tokenCb(token);
        }
        catch (err) {
            const { error } = await err.response.json()
            setError(error);

        }
    }

    return (
        <Formik
            initialValues={{firstName:'',lastName:'', email: '', password: '' }}
            onSubmit={register}
            validationSchema={RegisterSchema}
        >
            <Form>
                <h2>Register form</h2>
                <div className="form-group">
                    <Field className="form-control" type="text" name="firstName"  placeholder='First name'/>
                    <ErrorMessage className="alert alert-danger" component="div" name="firstName" />
                </div>
                <div className="form-group">
                    <Field className="form-control" type="text" name="lastName" placeholder='Last name'/>
                    <ErrorMessage className="alert alert-danger" component="div" name="lastName" />
                </div>
                <div className="form-group">
                    <Field className="form-control" type="email" name="email" placeholder='Email'/>
                    <ErrorMessage className="alert alert-danger" component="div" name="email" />
                </div>
                <div className="form-group">
                    <Field className="form-control" type="password" name="password" placeholder='Password'/>
                    <ErrorMessage className="alert alert-danger" component="div" name="password" />
                </div>
                <div className="form-group">
                    <button type='submit' className="btn btn-primary">Register</button>
                </div>
            </Form>
        </Formik>
    )
}
