import React from 'react';
import { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import NavBar from './NavBar';

function HistoryPicture(props) {
    const [picturs, setPicture] = useState();
    // const [url, setUrl] = useState("")
    // const [title, setTitle] = useState("")
    // const [explanation, setExplanation] = useState("")
    // const [mediaType, setMediaType] = useState("")


    useEffect(() => {
        var myHeaders = new Headers();
        myHeaders.append("Authorization", `${localStorage.getItem("token")}`);
console.log( `${localStorage.getItem("token")}`);
        var requestOptions = {
            method: 'GET',
            headers: myHeaders,
            redirect: 'follow'
        };

        fetch(`http://localhost:4200/getPictureByUserNameAndPassword/${props.name}/${props.password}`, requestOptions)
            .then(response => response.text())
            .then(result => {

                let result2 = JSON.parse(result)
                setPicture(result2);


                console.log(`history picture ${result2}`);

                // console.log(picturs);
                // console.log(picturs.user);
            })
            .catch(error => console.log('error', error));

    })

    return (
        <div>
            <NavBar />
            <div className="container">
                <div className="row">
                    <div className="col">
                        <h2>picture of the day</h2>
                        {
                            mediaType === "video" ?
                                <iframe src={`${url}`} alt="vidio" />
                                :
                                <img src={`${url}`} itemType={`${mediaType}`} alt="image today" />
                        }
                    </div>

                    <div className="col mt-5">
                        <h2>{title}</h2>
                        <p>{explanation}</p>
                    </div>

                </div>
            </div>
        </div>

    )
}

export default connect(
    (state) => {
        return {
            name: state.name,
            password: state.password,
            email: state.email,
            picture: state.picture
        }
    },
    null
)(HistoryPicture);
