import { keyBy } from 'lodash'
import { SET_MESSAGE } from '../actions/message.action'

const initialState = {
    messages: {},
}

export default function messageReducer(state = initialState, action) {
    switch (action.type) {
        case SET_MESSAGE:
            return { ...state, messages: keyBy(action.payload, (message) => message.id) }

       
        default:
            return state;
    }
}
