import { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import NavBar from './NavBar';
let i = 0;

function HistoryPicture(props) {
    const [picturs, setPicture] = useState();
    // const [url, setUrl] = useState("")
    // const [title, setTitle] = useState("")
    // const [explanation, setExplanation] = useState("")
    // const [mediaType, setMediaType] = useState("")


    if (i == 0) {
        let result = getAllPicture(props);
        setPicture(result)
        i = 1;
    }


    return (
        <div>
            <NavBar />
            <div className="container">
                <div className="row">
                    <h1>hi</h1>
                    {/* <div className="col">
                        <h2>picture of the day</h2>
                        {
                            mediaType === "video" ?
                                <iframe src={`${url}`} alt="vidio" />
                                :
                                <img src={`${url}`} itemType={`${mediaType}`} alt="image today" />
                        }
                    </div>

                    <div className="col mt-5">
                        <h2>{title}</h2>
                        <p>{explanation}</p>
                    </div> */}

                </div>
            </div>
        </div>

    )
}

export default connect(
    (state) => {
        return {
            name: state.name,
            password: state.password,
            email: state.email,
            picture: state.picture
        }
    },
    null
)(HistoryPicture);


const getAllPicture = (props) => {
    var myHeaders = new Headers();
    myHeaders.append("Authorization", `${localStorage.getItem("token")}`);
    console.log(`${localStorage.getItem("token")}`);
    var requestOptions = {
        method: 'GET',
        headers: myHeaders,
        redirect: 'follow'
    };
console.log(`${props.name}/${props.password}`);
    fetch(`http://localhost:4200/getPictureByUserNameAndPassword/${props.name}/${props.password}`, requestOptions)
        .then(response => response.text())
        .then(result => {

            let result2 = JSON.parse(result)


            console.log(`history picture ${result2.pi}`);
            // console.log(`history picture ${result2.user}`);

            return (result2)

            // console.log(picturs);
            // console.log(picturs.user);
        })
        .catch(error => console.log('error', error));
}
